import React from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";

import CustomButton from "../custom-button/custom-button.component";
import { addItem } from "../../redux/cart/cart.actions";

import "./collection-item.styles.scss";

const CollectionItem = ({ item, addItem, match, historyt }) => {
	const { name, price, imageUrl, routeName } = item;
	console.log(match.url, routeName);
	return (
		<div className="collection-item">
			<Link
				to={routeName ? `${match.url}/${routeName}` : `${match.url}`}
				className="image"
				style={{
					backgroundImage: `url(${imageUrl})`,
				}}
			/>
			<div className="collection-footer">
				<span className="name">{name}</span>
				<span className="price">&#x20B9;{price}</span>
			</div>
			{routeName ? "" : <CustomButton inverted onClick={() => addItem(item)}>
				ADD TO CART
			</CustomButton>}
		</div>
	);
};

const mapDispatchToProps = (dispatch) => ({
	addItem: (item) => dispatch(addItem(item)),
});

export default withRouter(connect(null, mapDispatchToProps)(CollectionItem));
