import { createSelector } from "reselect";
// import memoize from "lodash.memoize";

const selectShop = (state) => state.shop;

export const selectCollections = createSelector([selectShop], (shop) => shop.collections);

export const selectCollectionsForPreview = createSelector([selectCollections], (collections) =>
	Object.keys(collections).map((key) => collections[key])
);

export const selectCollection = (collectionUrlParam) =>
	createSelector([selectCollections], (collections) => collections[collectionUrlParam]);

export const selectProducts = (productUrlParam) =>{ (console.log(productUrlParam))
	createSelector([selectCollection], (items) => items[productUrlParam])};

// export const selectProducts = createSelector([selectCollections], (collection) => collection["oils"].items.map(i => i.products));

export const selectProduct = (productUrlParam) =>
	createSelector([selectProducts], (products) => products[productUrlParam]);
