import React from "react";

import FormInput from "../../components/form-input/form-input.component";
import CustomButton from "../../components/custom-button/custom-button.component";

import { ContactContainer, ContactTitle, ContactForm, AddressInfo } from "./contact.styles";

class ContactPage extends React.Component {
	constructor() {
		super();

		this.state = {
			name: "",
			email: "",
			message: "",
		};
	}

	handleChange = (event) => {
		const { name, value } = event.target;

		this.setState({ [name]: value });
	};

	render() {
        const { name, email, message } = this.state;
		return (
			<ContactContainer>
				<ContactTitle>Contact Us</ContactTitle>
				<span>
					Please get in touch and our expert support team will answer all your questions.
				</span>
				<ContactForm>
					<FormInput
						type="text"
						name="name"
						value={name}
						onChange={this.handleChange}
						label="Name"
						required
					/>
					<FormInput
						type="email"
						name="email"
						value={email}
						onChange={this.handleChange}
						label="Email"
						required
					/>
					<FormInput
						type="textarea"
						name="message"
						value={message}
						onChange={this.handleChange}
						label="Message"
						required
					/>
					<CustomButton type="submit">SEND EMAIL</CustomButton>
				</ContactForm>
				<AddressInfo>
					<span>Address:</span>
					Shree Ramkrishna Oil Mills Mayur Colony, Near P-JOG High School, Kothrud Area,
					Pune 411038. Telephone: +91 8007000739
				</AddressInfo>
			</ContactContainer>
		);
	}
}

export default ContactPage;
