import React from 'react';

import MenuItem from '../menu-item/menu-item.component';
import Flour from '../../images/Flour.jpg';

import './directory.styles.scss';

class Directory extends React.Component {
    constructor() {
        super();

        this.state = {
			sections: [
				{
					title: "Cooking Oils",
					imageUrl:
						"https://i.ndtvimg.com/i/2016-09/groundnut-oil_625x350_71472799035.jpg",
					id: 1,
				},
				{
					title: "Body Oils",
					imageUrl: "https://i.ibb.co/px2tCc3/jackets.png",
					id: 2,
					linkUrl: "shop/jackets",
				},
				{
					title: "Scrubs",
					imageUrl: "https://i.ibb.co/0jqHpnp/sneakers.png",
					id: 3,
					linkUrl: "shop/sneakers",
				},
				{
					title: "Flours",
					imageUrl:
						"https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/almond-flour-with-three-nuts-on-top-royalty-free-image-1579203952.jpg",
					size: "large",
					id: 4,
				},
				{
					title: "Other",
					imageUrl: "https://i.ibb.co/R70vBrQ/men.png",
					size: "large",
					id: 5,
					linkUrl: "shop/mens",
				},
			],
		};
    }

    render() {
        return (
            <div className="directory-menu">
                {this.state.sections.map(({title, imageUrl, id, size}) => (
                    <MenuItem key = {id} title = {title} imageUrl = {imageUrl} size = {size}/>
                ))}
            </div>
        );
    }
}

export default Directory;
