import { createSelector } from "reselect";
// import memoize from "lodash.memoize";

const selectShop = (state) => state.shop;

export const selectCollections = createSelector([selectShop], (shop) => shop.collections);

export const selectCollectionsForPreview = createSelector([selectCollections], (collections) =>
	Object.keys(collections).map((key) => collections[key])
);

export const selectCollection = (collectionUrlParam) =>
	createSelector([selectCollections], (collections) => collections[collectionUrlParam]);

export const createSelectProducts = (collectionUrlParam) => {
	const collections = selectCollection(collectionUrlParam);

	return (productUrlParam) => createSelector([], collections[productUrlParam]);
};

export const selectProducts = (collectionUrlParam, productUrlParam) =>
	createSelector([selectCollections], (collections) => collections[collectionUrlParam].items[productUrlParam]); 


const selectProductsCurried = (coll) => (prod) =>
	createSelector([selectCollections], (collections) => collections[coll]);


// export const selectProducts = createSelector([selectCollections], (collection) => collection["oils"].items.map(i => i.products));

export const selectProduct = (collectionUrlParam, productUrlParam) =>
	createSelector(
		[selectCollections],
		(collections) => collections[collectionUrlParam].items[productUrlParam].products
	);
