import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import CollectionItem from "../../components/collection-item/collection-item.component";
// import ProductInformation  from '../../components/product-information/product-information.component';

import { selectProducts, createSelectProducts } from "../../redux/shop/shop.selectors";

import {
	ProductPageContainer,
	ProductTitle,
	ProductItemsContainer,
} from "./product.styles";

const ProductPage = ({ collection, prod }) => {
	console.log(collection);
	console.log(prod[0]);
	// const { title } = items;
	return (
		<ProductPageContainer>
			<ProductTitle>OIL</ProductTitle>
			{/* {Object.entries(description).map(([key, val]) => 
				<ProductInformation h2 = {h2} p = {val.p} li = {val.li} />
			)} */}
			{/* <ProductInformation description={description} /> */}
			{/* <ProductItemsContainer>
				{products.map((product) => (
					<CollectionItem key={product.id} item={product} />
				))}
			</ProductItemsContainer> */}
		</ProductPageContainer>
	);
};

// const mapStateToProps = createStructuredSelector({
// 	items: selectProducts,
// });

const mapStateToProps = (state, ownProps) => ({
	collection: selectProducts(
		ownProps.match.params.collectionId,
		ownProps.match.params.productId
	)(state),
	prod: createSelectProducts("oils")
});

export default connect(mapStateToProps)(ProductPage);
