import styled from "styled-components";

export const FooterContainer = styled.div`
	background: #fdfdb4;
	color: rgba(0, 0, 0, 0.25);
	font-size: 14px;
	font-weight: bold;
	text-transform: uppercase;
	letter-spacing: 1px;
	padding: 10px;
`;
