import React from 'react';

import MenuItem from '../menu-item/menu-item.component';
import OilBanner from '../../images/oil-banner.jpg';
import BodyOilBanner from "../../images/body-oil-banner.jpg";
import ScrubBanner from '../../images/scrub-banner.jpg';

import './directory.styles.scss';

class Directory extends React.Component {
    constructor() {
        super();

        this.state = {
			sections: [
				{
					title: "Cooking Oils",
					imageUrl: OilBanner,
					id: 1,
				},
				{
					title: "Body Oils",
					imageUrl: BodyOilBanner,
					id: 2,
				},
				{
					title: "Scrubs",
					imageUrl: ScrubBanner,
					id: 3,
				},
				{
					title: "Flours",
					imageUrl: FlourBanner,
					size: "large",
					id: 4,
				},
				{
					title: "Other",
					imageUrl:
						"https://draxe.com/wp-content/uploads/2017/01/Pink-Himalayan-Salt_HEADER.jpg",
					size: "large",
					id: 5,
				},
			],
		};
    }

    render() {
        return (
            <div className="directory-menu">
                {this.state.sections.map(({title, imageUrl, id, size}) => (
                    <MenuItem key = {id} title = {title} imageUrl = {imageUrl} size = {size}/>
                ))}
            </div>
        );
    }
}

export default Directory;
